package corejava;

public class Boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean user=true;
		if (user==true)
		{
			System.out.println("it is true");
		}
		else
		{
			System.out.println("it is false");
		}
		boolean user1=true;
		if(!user1)
		{
			System.out.println("it is false");	
		}
		else
		{
			System.out.println("it is true");
		}
	}

}
