package corejava;

public class weather {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int weather1=12;
		switch (weather1)
				{
		case 12:
			System.out.println("cloudy");
			break;
		case -5:
			System.out.println("freezing");
			break;
		case 60:
			System.out.println("hot");
			break;
		}

	}

}
